mapper.select:
select 

from book
where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}

mapper.delete:
delete from book
where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}

mapper.update:
update book


    describe = #{describe,jdbcType=VARCHAR},


    write = #{write,jdbcType=VARCHAR},


where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}

update book
set describe = #{describe,jdbcType=VARCHAR},
  write = #{write,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and name = #{name,jdbcType=VARCHAR}
